int main(int argc, char* argv[])
{
	(void)argc;
	(void)argv;

    GHandle gh;

	gfxInit();
    
    palSetPadMode(GPIOA,0,PAL_MODE_INPUT_ANALOG);
    adcStart(&ADCD1, NULL);
    chThdCreateStatic(wa_adcThread, sizeof(wa_adcThread), NORMALPRIO, adcThread, NULL);

	palSetPadMode(GPIOE,5,PAL_MODE_OUTPUT_PUSHPULL);
	chThdCreateStatic(waBlink, sizeof(waBlink), NORMALPRIO, thdBlink, NULL);
    chThdCreateStatic(waGenData, sizeof(waGenData), NORMALPRIO, thdGenData, NULL);


    {
        GWindowInit wi;

        wi.show = TRUE;
        wi.x = wi.y = 0;
        wi.width = gdispGetWidth();
        wi.height = gdispGetHeight();
        gh = gwinGraphCreate(0, &wi);
    }

    while(TRUE) {
        gwinClear(gh);
        gwinGraphSetOrigin(gh, gwinGetWidth(gh)/8, gwinGetHeight(gh)/8);
        gwinGraphSetStyle(gh, &GraphStyle1);
        gwinGraphDrawAxis(gh);

        gwinGraphStartSet(gh);
        gwinGraphSetStyle(gh, &GraphStyle2);
        gwinGraphDrawAxis(gh);

        gwinGraphDrawPoints(gh, data, sizeof(data)/sizeof(data[0]));
        gfxSleepMilliseconds(10);
    }

	return 0;
}
